  @Override  public void setVersions(final VersionTag versionTag) {    memberId = versionTag.getMemberID();    int eVersion = versionTag.getEntryVersion();    entryVersionLowBytes = (short) (eVersion & 0xffff);    entryVersionHighByte = (byte) ((eVersion & 0xff0000) >> 16);    regionVersionHighBytes = versionTag.getRegionVersionHighBytes();    regionVersionLowBytes = versionTag.getRegionVersionLowBytes();    if (!versionTag.isGatewayTag()        && distributedSystemId == versionTag.getDistributedSystemId()) {      if (getVersionTimeStamp() <= versionTag.getVersionTimeStamp()) {        setVersionTimeStamp(versionTag.getVersionTimeStamp());      } else {        versionTag.setVersionTimeStamp(getVersionTimeStamp());      }    } else {      setVersionTimeStamp(versionTag.getVersionTimeStamp());    }    distributedSystemId = (byte) (versionTag.getDistributedSystemId() & 0xff);  }